item.description:
 Sorting Merge sort is comparison based sorting.
 Algo : Divide and Conquer Time complexity : O(nlog(n)) Space Complexity : O(n)
public void mergeSort(int[] arr) { //Error handling if(arr == null || arr.length == 0) return; int[] aux = new int[arr.length];	mergeSort(arr, aux, 0, arr.length-1);	} public void mergeSort(int[] arr, int[] aux, int left, int right) { if(left &lt; right) { int mid = left + (right - left)/2; //Divide mergeSort(arr, aux, left, mid); mergeSort(arr, aux, mid+1, right); merge(arr, aux, left, mid, right); } } //Merge two sorted array public void merge(int[] arr, int[] aux, int left, int mid, int right) { int i = left, j = mid + 1, k = left; while(i &lt;= mid &amp;&amp; j &lt;= right) { if(arr[i] &lt; arr[j]) aux[k] = arr[i++]; else aux[k] = arr[j++]; k++; } while(i &lt;= mid) { aux[k++] = arr[i++]; } while(j &lt;= right) { aux[k++] = arr[j++]; } for(int p = left; p &lt;= right; p++) { arr[p] = aux[p]; } }
